package java_practice;

public class if_else_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=45;
		int b=212;
		
		boolean flag=a>b;
		
		System.out.println(flag);
		
		if (flag) {
			
			System.out.println("a is larger");
		}
		else {
			
			System.out.println("b is larger");
		}
			
		

	}

}
