package java_handson;

public class switch_string_case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="dinesh";
		switch(name)
		{
		case "srinath":
			System.out.println("Found srinath");
			break;
		case "dinesh":
			System.out.println("Found dinesh");
			break;
			
		case "sai":
			System.out.println("Found sai");
			break;
			
		case "shiva":
			System.out.println("Found shiva");
			break;
		
			
			
		default:
			System.out.println("default case");
			
		}
		

	}

}
